let controller;
let slideScene;
let pageScene;


function animateSlides() {
    // init controller
    controller = new ScrollMagic.Controller();
    // Select e
    const sliders = document.querySelectorAll('.slide');
    const header = document.querySelector('.nav-header');
    // loop over slides
    sliders.forEach((slide, index, slides) => {
        const revealImg = slide.querySelector('.reveal-img');
        const img = slide.querySelector('img');
        const revealText = slide.querySelector('.reveal-text');
        //GSAP
        const slideTl = gsap.timeline({
            defaults: { duration: 1, ease: "slow" }
        });
        slideTl.fromTo(revealImg, { x: '0%' }, { x: '100%' });
        slideTl.fromTo(img, { scale: 2 }, { scale: 1 }, '-=1');
        slideTl.fromTo(revealText, { y: '0%' }, { y: '100%' }, '-=.85');
        slideTl.fromTo(header, { y: '-100%' }, { y: '0%' });

        // create scene
        slideScene = new ScrollMagic.Scene({
            triggerElement: slide,
            triggerHook: .5,
            reverse: false
        })
            .setTween(slideTl)
            .addIndicators({ colorStart: 'white', colorTrigger: 'white', name: 'slide' })
            .addTo(controller);

        // new animation
        const pageTl = gsap.timeline();
        let nextSlide = slides.length - 1 === index ? 'end' : slides[index + 1];
        pageTl.fromTo(nextSlide, { y: '0%' }, { y: '50%' })
        pageTl.fromTo(slide, { opacity: 1, scale: 1 }, { opacity: 0, scale: .5 });
        pageTl.fromTo(nextSlide, { y: '50%' }, { y: '0%' }, '-=.5')

        // create new scene
        pageScene = new ScrollMagic.Scene({
            triggerElement: slide,
            duration: '100%',
            triggerHook: 0,
        })
            .addIndicators({ colorStart: 'white', colorTrigger: 'white', name: 'page', indent: 200 })
            .setPin(slide, { pushFollowers: false })
            .setTween(pageTl)
            .addTo(controller)
    });
}

const mouse = document.querySelector('.cursor');
const mouseTxt = mouse.querySelector('span');
const burger = document.querySelector('.burger')

function cursor(e) {

    mouse.style.top = e.pageY + 'px';
    mouse.style.left = e.pageX + 'px';
}

function activeCursor(e) {
    const item = e.target;
    if (item.id === 'logo' || item.classList.contains('burger')) {
        mouse.classList.add('nav-active')
    } else {
        mouse.classList.remove('nav-active')
    }
    if (item.classList.contains('explore')) {
        mouse.classList.add('explore-active');
        gsap.to('.title-swipe', 1, { y: '0%' });
        mouseTxt.innerText = 'Tap';
    } else {
        mouse.classList.remove('explore-active');
        mouseTxt.innerText = '';
        gsap.to('.title-swipe', 1, { y: '100%' });
    }
}

function navToggle(e) {
    if (!e.target.classList.contains('active')) {
        e.target.classList.add('active');
        gsap.to('.line1', 0.5, { rotate: '45', y: 5, backgroundColor: "black" });
        gsap.to('.line2', 0.5, { rotate: '-45', y: -5, backgroundColor: 'black' });
        gsap.to('#logo', 1, { color: 'black' });
        gsap.to('.navbar', 1, { clipPath: 'circle(2500px at 100% -10%)' });
        document.body.classList.add('.hide');
    } else {
        e.target.classList.remove('active');
        gsap.to('.line1', 0.5, { rotate: '0', y: 0, backgroundColor: "white" });
        gsap.to('.line2', 0.5, { rotate: '0', y: -0, backgroundColor: 'white' });
        gsap.to('#logo', 1, { color: 'white' });
        gsap.to('.navbar', 1, { clipPath: 'circle(50px at 100% -10%)' });
        document.body.classList.remove('.hide');
    }
}

// Event Listner

window.addEventListener('mousemove', cursor);
window.addEventListener('mousemove', activeCursor);
burger.addEventListener('click', navToggle);

animateSlides();
